import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Product } from '../Models/Product';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
  providers:[ProductService]
})
export class ProductsComponent implements OnInit{
  error= false
  productList!:Product[];
  prix!:number;
   
    constructor(private R: Router, private productS:ProductService){}
      
    ngOnInit(): void {
        this.productS.getAllproducts().subscribe(
          data =>  this.productList=data 
          );
      }



    Buy(P:Product){
     // if( P.quantity>0){
        P.quantity--;
     // }

    }
    Like(P:Product){
      P.like++;
    }

    showDetails(id:number){
this.R.navigate(['productDetails', id]);
    }


    delete(P: Product){
      this.productS.deleteProduct(P.id).subscribe(()=> {alert('Deleted!');})
      
    }
}
<h2 class="center container"> List Products</h2>
search: <input type="text" [(ngModel)]="prix">
<section  *ngFor="let P of productList, let i=index"  >
   
   <div  *ngIf="P.price <= prix || !prix"        class="card" style="width: 18rem;"  >
    <img class="card-img-top" src="../../assets/download.jfif" alt="Card image cap">
    <div class="card-body">
      <h5 class="card-title">{{P.title}}</h5>
      <h5>{{P.price}}</h5>
      
      <button class="btn btn-primary"  (click)="Buy(P)"  [disabled]="P.quantity==0">Buy</button>
      <button class="btn btn-primary"  (click)="delete(P)" >Delete</button>
      <button class="btn btn-primary"  (click)="Like(P)" >Like</button>
      <button class="btn btn-primary"  (click)="showDetails(P.id)" >Details</button>
      <button [routerLink]="['/productDetails',P.id, i]">Show Details</button>
    </div>
  </div>
</section>

<section>
 <button class="btn btn-primary"  routerLink="/addProduct"  >Add New Product</button> 
</section>
